import { Component, OnInit } from '@angular/core';
import { IPersona } from '../ipersona';
import { PersonDataSource } from '../PersonDataSource';
import { PersonaService } from '../Services/persona.service';
/**
 * @title Basic use of `<mat-table>` (uses display flex)
 */
@Component({
  selector: 'app-consulta',
  templateUrl: './consulta.component.html',
  styleUrls: ['./consulta.component.css']
})
export class ConsultaComponent implements OnInit {

  $personas!:IPersona[];
  dataSource!: PersonDataSource;
  displayedColumns:string[]= ["id","name", "phoneNumber",
  // "email",
 // "city","street","number"
]
  ;


  constructor(private _personaService: PersonaService) { };



  ngOnInit() {
    this.dataSource = new PersonDataSource(this._personaService);
    this.dataSource.loadPeople();
}

}
<div class="spinner-container" *ngIf="dataSource.loading$ | async">
  <mat-spinner></mat-spinner>
</div>
<button mat-raised-button color [routerLink]="['./alta']"="accent">Agregar persona</button>
 <mat-table [dataSource]="dataSource" class="mat-elevation-z8"> -->
  <!-- Position Column -->
   <ng-container matColumnDef="id">
    <mat-header-cell *matHeaderCellDef> ID </mat-header-cell>
    <mat-cell *matCellDef="let person"> {{person.id}} </mat-cell>
  </ng-container>

   Name Column
  <ng-container matColumnDef="name">
    <mat-header-cell *matHeaderCellDef> Nombre </mat-header-cell>
    <mat-cell *matCellDef="let person"> {{person.name}} </mat-cell>
  </ng-container>

  <!-- Weight Column -->
   <ng-container matColumnDef="phoneNumber">
    <mat-header-cell *matHeaderCellDef> Numero de Telefono </mat-header-cell>
    <mat-cell *matCellDef="let person"> {{person.phoneNumber}} </mat-cell>
  </ng-container>

  <!-- Symbol Column -->
  <!-- <ng-container matColumnDef="symbol">
    <mat-header-cell *matHeaderCellDef> Symbol </mat-header-cell>
    <mat-cell *matCellDef="let element"> {{element.symbol}} </mat-cell>
  </ng-container> -->

   <mat-header-row *matHeaderRowDef="displayedColumns"></mat-header-row>
  <mat-row *matRowDef="let row; columns: displayedColumns;"></mat-row>
</mat-table>
