import { Component, OnInit } from '@angular/core';
import { MatTableDataSource } from '@angular/material/table';
import { map, Observable } from 'rxjs';
import { IPersona } from '../ipersona';
import { PersonDataSource } from '../PersonDataSource';
import { PersonaService } from '../Services/persona.service';
/**
 * @title Basic use of `<mat-table>` (uses display flex)
 */
@Component({
  selector: 'app-consulta',
  templateUrl: './consulta.component.html',
  styleUrls: ['./consulta.component.css']
})
export class ConsultaComponent implements OnInit {


  dataSource!: PersonDataSource;
  displayedColumns= ["Nombre", "Telefono", "Email", "Ciudad","Direccion"];


  constructor(private _personaService: PersonaService) { };



  ngOnInit() {
    this.dataSource = new PersonDataSource(this._personaService);
    this.dataSource.loadPeople();
}

}
<div class="spinner-container" *ngIf="dataSource.loading$ | async">
  <mat-spinner></mat-spinner>
</div>
<mat-table [dataSource]="dataSource" class="mat-elevation-z8">
  <!-- Position Column -->
  <ng-container matColumnDef="position">
    <mat-header-cell *matHeaderCellDef> No. </mat-header-cell>
    <mat-cell *matCellDef="let person"> {{person.position}} </mat-cell>
  </ng-container>

  <!-- Name Column -->
  <ng-container matColumnDef="name">
    <mat-header-cell *matHeaderCellDef> Name </mat-header-cell>
    <mat-cell *matCellDef="let person"> {{person.name}} </mat-cell>
  </ng-container>

  <!-- Weight Column -->
